import React, { useState } from "react";

export default function TournamentForm() {
  const [formData, setFormData] = useState({
    tournamentsname: "",
    description: "",
    logoUrl: "",
    startDate: "",
    endDate: "",
    organizerName: "",
    organizerPhone: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const tournamentData = {
      ...formData,
      createdAt: new Date().toISOString()
    };

    try {
      const response = await fetch("http://localhost:8080/tournament/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(tournamentData)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      alert("Tournament created successfully!");
      setFormData({
        tournamentsname: "",
        description: "",
        logoUrl: "",
        startDate: "",
        endDate: "",
        organizerName: "",
        organizerPhone: ""
      });
    } catch (error) {
      console.error("Error creating tournament", error);
      alert("Error creating tournament");
    }
  };

  const styles = {
    container: {
      maxWidth: "500px",
      margin: "30px auto",
      padding: "20px",
      backgroundColor: "#f9f9f9",
      borderRadius: "8px",
      boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)"
    },
    title: {
      textAlign: "center",
      color: "#333",
      marginBottom: "20px"
    },
    label: {
      display: "block",
      marginBottom: "6px",
      fontWeight: "bold",
      color: "#444"
    },
    input: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "14px"
    },
    textarea: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "14px",
      minHeight: "80px"
    },
    button: {
      width: "100%",
      padding: "12px",
      backgroundColor: "#4CAF50",
      color: "#fff",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold"
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Create Tournament</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Tournament Name:</label>
        <input
          type="text"
          name="tournamentsname"
          value={formData.tournamentsname}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Description:</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          style={styles.textarea}
        ></textarea>

        <label style={styles.label}>Logo URL:</label>
        <input
          type="text"
          name="logoUrl"
          value={formData.logoUrl}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Start Date:</label>
        <input
          type="date"
          name="startDate"
          value={formData.startDate}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>End Date:</label>
        <input
          type="date"
          name="endDate"
          value={formData.endDate}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Organizer Name:</label>
        <input
          type="text"
          name="organizerName"
          value={formData.organizerName}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Organizer Phone:</label>
        <input
          type="text"
          name="organizerPhone"
          value={formData.organizerPhone}
          onChange={handleChange}
          style={styles.input}
        />

        <button type="submit" style={styles.button}>
          Save Tournament
        </button>
      </form>
    </div>
  );
}
