import React, { useState } from "react";

export default function TournamentForm() {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    logoUrl: "",
    startDate: "",
    endDate: "",
    organizerName: "",
    organizerPhone: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const tournamentData = {
      ...formData,
      createdAt: new Date().toISOString() // LocalDateTime format
    };

    try {
      const response = await fetch("http://localhost:8080/tournaments", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(tournamentData)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      alert("Tournament created successfully!");
      setFormData({
        name: "",
        description: "",
        logoUrl: "",
        startDate: "",
        endDate: "",
        organizerName: "",
        organizerPhone: ""
      });
    } catch (error) {
      console.error("Error creating tournament", error);
      alert("Error creating tournament");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto" }}>
      <h2>Create Tournament</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

        <label>Description:</label>
        <textarea name="description" value={formData.description} onChange={handleChange}></textarea>

        <label>Logo URL:</label>
        <input type="text" name="logoUrl" value={formData.logoUrl} onChange={handleChange} />

        <label>Start Date:</label>
        <input type="date" name="startDate" value={formData.startDate} onChange={handleChange} />

        <label>End Date:</label>
        <input type="date" name="endDate" value={formData.endDate} onChange={handleChange} />

        <label>Organizer Name:</label>
        <input type="text" name="organizerName" value={formData.organizerName} onChange={handleChange} />

        <label>Organizer Phone:</label>
        <input type="text" name="organizerPhone" value={formData.organizerPhone} onChange={handleChange} />

        <br /><br />
        <button type="submit">Save Tournament</button>
      </form>
    </div>
  );
}
