import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");
  const [role, setRole] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [showLogoutConfirmModal, setShowLogoutConfirmModal] = useState(false);

  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn") === "true";
    const storedName = localStorage.getItem("name") || "";
    const storedRole = localStorage.getItem("role") || "";
    setIsLoggedIn(loggedIn);
    setUserName(storedName.toUpperCase());
    setRole(storedRole);
  }, []);

  const showModalMessage = (message) => {
    setModalMessage(message);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setModalMessage("");
  };

  const handleLoginLogout = () => {
    if (isLoggedIn) {
      setShowLogoutConfirmModal(true);
    } else {
      navigate("/login");
    }
  };

  const confirmLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    setUserName("");
    setRole("");
    setShowLogoutConfirmModal(false);
    setTimeout(() => navigate("/"), 1000);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow fixed-top">
        <div className="container-fluid">
          <span className="navbar-brand">
            {!isLoggedIn ? "Cricket App" : `Welcome, ${userName}`}
          </span>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">

              {/* Tournament Menu */}
              <li className="nav-item">
                <Link className="nav-link" to="/">Tournament</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/team">Team</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/player">Player</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/match">Match</Link>
              </li>

             

              {/* Admin Controls */}
              {role.toLowerCase() === "admin" && (
                <>
                  <button
                    className="btn btn-outline-warning me-2 my-1"
                    onClick={() => navigate("/myAdmin")}
                  >
                    Admin Panel
                  </button>
                  <button
                    className="btn btn-outline-light me-2 my-1"
                    onClick={() => navigate("/myUser")}
                  >
                    Manage Users
                  </button>
                </>
              )}

              {/* Login/Logout */}
              <button
                className={`btn ${isLoggedIn ? "btn-danger" : "btn-outline-warning"} ms-2`}
                onClick={handleLoginLogout}
              >
                {isLoggedIn ? "Logout" : "Login"}
              </button>

              {/* Register Button */}
              {!isLoggedIn && (
                <li className="nav-item">
                  <Link className="btn btn-outline-light ms-2" to="/register">
                    Register
                  </Link>
                </li>
              )}
            </ul>
          </div>
        </div>
      </nav>

      {/* Notice Modal */}
      {showModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Notice</h5>
                <button type="button" className="btn-close" onClick={closeModal}></button>
              </div>
              <div className="modal-body">
                <p>{modalMessage}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Logout Confirmation Modal */}
      {showLogoutConfirmModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-danger text-white">
                <h5 className="modal-title">Confirm Logout</h5>
              </div>
              <div className="modal-body">
                <p>Are you sure you want to log out?</p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowLogoutConfirmModal(false)}>Cancel</button>
                <button className="btn btn-danger" onClick={confirmLogout}>Yes, Logout</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
