import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";

export default function TeamForm() {
  const [formData, setFormData] = useState({
    tournamentId: "",
    teamname: "",
    logoUrl: "",
    city: "",
    captainName: "",
    captainPhone: ""
  });

  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await fetch("http://localhost:8080/tournament/fetch");
        if (!response.ok) throw new Error("Failed to fetch tournaments");
        const data = await response.json();
        setTournaments(data);
      } catch (error) {
        console.error("Error fetching tournaments", error);
      }
    };
    fetchTournaments();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Step 1: Check if this captain already has a team in the selected tournament
      const checkResponse = await fetch(
        `http://localhost:8081/team/byTournament/${formData.tournamentId}`
      );
      if (!checkResponse.ok) throw new Error("Failed to fetch teams for tournament");
      const existingTeams = await checkResponse.json();

      const duplicate = existingTeams.some(
        (team) =>
          team.captainName?.toLowerCase() === formData.captainName.toLowerCase() ||
          team.captainPhone === formData.captainPhone
      );

      if (duplicate) {
        alert("This captain is already registered in this tournament!");
        return;
      }

      // Step 2: Proceed to create the team
      const response = await fetch("http://localhost:8081/team/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      alert("Team created successfully!");
      setFormData({
        tournamentId: "",
        teamname: "",
        logoUrl: "",
        city: "",
        captainName: "",
        captainPhone: ""
      });
    } catch (error) {
      console.error("Error creating team", error);
      alert("Error creating team");
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow p-4">
        <h2 className="text-center mb-4">Register Team</h2>
        <form onSubmit={handleSubmit}>
          {/* Tournament Select */}
          <div className="mb-3">
            <label className="form-label">Tournament</label>
            <select
              name="tournamentId"
              value={formData.tournamentId}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">-- Select Tournament --</option>
              {tournaments.map((tournament) => (
                <option key={tournament.id} value={tournament.id}>
                  {tournament.id} - {tournament.tournamentsname}
                </option>
              ))}
            </select>
          </div>

          {/* Team Name */}
          <div className="mb-3">
            <label className="form-label">Team Name</label>
            <input
              type="text"
              name="teamname"
              value={formData.teamname}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          {/* Logo URL */}
          <div className="mb-3">
            <label className="form-label">Logo URL</label>
            <input
              type="text"
              name="logoUrl"
              value={formData.logoUrl}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          {/* City */}
          <div className="mb-3">
            <label className="form-label">City</label>
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          {/* Captain Name */}
          <div className="mb-3">
            <label className="form-label">Captain Name</label>
            <input
              type="text"
              name="captainName"
              value={formData.captainName}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          {/* Captain Phone */}
          <div className="mb-3">
            <label className="form-label">Captain Phone</label>
            <input
              type="text"
              name="captainPhone"
              value={formData.captainPhone}
              onChange={handleChange}
              className="form-control"
            />
          </div>

          {/* Submit Button */}
          <button type="submit" className="btn btn-primary w-100">
            Save Team
          </button>
        </form>
      </div>
      <br />
      <button className="btn btn-secondary">
        <Link className="nav-link" to="/">
          Back
        </Link>
      </button>
    </div>
  );
}
