import React, { useState } from "react";

export default function MatchForm() {
  const [formData, setFormData] = useState({
    tournamentId: "",
    teamAId: "",
    teamBId: "",
    scheduledAt: "",
    venue: "",
    status: "",
    result: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8080/matches/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      alert("Match created successfully!");
      setFormData({
        tournamentId: "",
        teamAId: "",
        teamBId: "",
        scheduledAt: "",
        venue: "",
        status: "",
        result: ""
      });
    } catch (error) {
      console.error("Error creating match", error);
      alert("Error creating match");
    }
  };

  const styles = {
    container: {
      maxWidth: "500px",
      margin: "30px auto",
      padding: "20px",
      backgroundColor: "#f9f9f9",
      borderRadius: "8px",
      boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)"
    },
    title: {
      textAlign: "center",
      color: "#333",
      marginBottom: "20px"
    },
    label: {
      display: "block",
      marginBottom: "6px",
      fontWeight: "bold",
      color: "#444"
    },
    input: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "14px"
    },
    select: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "14px"
    },
    button: {
      width: "100%",
      padding: "12px",
      backgroundColor: "#4CAF50",
      color: "#fff",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold"
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Create Match</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Tournament ID:</label>
        <input
          type="number"
          name="tournamentId"
          value={formData.tournamentId}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Team A ID:</label>
        <input
          type="number"
          name="teamAId"
          value={formData.teamAId}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Team B ID:</label>
        <input
          type="number"
          name="teamBId"
          value={formData.teamBId}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Scheduled At:</label>
        <input
          type="datetime-local"
          name="scheduledAt"
          value={formData.scheduledAt}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Venue:</label>
        <input
          type="text"
          name="venue"
          value={formData.venue}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Status:</label>
        <select
          name="status"
          value={formData.status}
          onChange={handleChange}
          style={styles.select}
          required
        >
          <option value="">-- Select Status --</option>
          <option value="SCHEDULED">Scheduled</option>
          <option value="COMPLETED">Completed</option>
          <option value="CANCELLED">Cancelled</option>
        </select>

        <label style={styles.label}>Result:</label>
        <input
          type="text"
          name="result"
          value={formData.result}
          onChange={handleChange}
          style={styles.input}
        />

        <button type="submit" style={styles.button}>
          Save Match
        </button>
      </form>
    </div>
  );
}
