import React, { useEffect, useState, useCallback } from "react";

export default function CaptainManage() {
  const [captains, setCaptains] = useState([]);
  const [selectedCaptain, setSelectedCaptain] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch all captains
  const fetchCaptains = useCallback(() => {
    fetch(`http://localhost:8081/user/captain`) // ✅ Change to your real endpoint
      .then((res) => res.json())
      .then((data) => setCaptains(data))
      .catch((err) => console.error("Error fetching captains:", err));
  }, []);

  useEffect(() => {
    fetchCaptains();
  }, [fetchCaptains]);

  const deleteCaptain = (email) => {
    if (window.confirm("Are you sure you want to delete this captain?")) {
      fetch(`http://localhost:8081/user/delete/${email}`, { method: "DELETE" })
        .then(() => {
          alert("Captain deleted successfully!");
          fetchCaptains();
        })
        .catch((err) => console.error("Error deleting captain:", err));
    }
  };

  const saveCaptain = () => {
    fetch(`http://localhost:8081/user/update/${selectedCaptain.email}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(selectedCaptain),
    })
      .then(() => {
        alert("Captain updated successfully!");
        setShowModal(false);
        fetchCaptains();
      })
      .catch((err) => console.error("Error updating captain:", err));
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-primary">Captain Manage</h2>

      <div className="row">
        {captains.map((captain) => (
          <div className="col-md-4 mb-4" key={captain.email}>
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <h5 className="card-title">{captain.name}</h5>
                <p className="text-muted">📧 {captain.email}</p>
                <p>📞 {captain.mobile}</p>
                <p>🎯 Role: {captain.role}</p>
              </div>
              <div className="card-footer d-flex justify-content-between">
                <button
                  className="btn btn-warning btn-sm"
                  onClick={() => {
                    setSelectedCaptain(captain);
                    setShowModal(true);
                  }}
                >
                  ✏ Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteCaptain(captain.email)}
                >
                  🗑 Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {captains.length === 0 && (
        <p className="text-center text-muted">No captains found.</p>
      )}

      {/* Modal */}
      {showModal && selectedCaptain && (
        <div
          className="modal fade show"
          style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Captain</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <label>Name</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedCaptain.name}
                  onChange={(e) =>
                    setSelectedCaptain({
                      ...selectedCaptain,
                      name: e.target.value,
                    })
                  }
                />
                <label>Email</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedCaptain.email}
                  readOnly
                />
                <label>Mobile</label>
                <input
                  type="number"
                  className="form-control mb-2"
                  value={selectedCaptain.mobile}
                  onChange={(e) =>
                    setSelectedCaptain({
                      ...selectedCaptain,
                      mobile: e.target.value,
                    })
                  }
                />
                <label>Role</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedCaptain.role}
                  readOnly
                />
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Close
                </button>
                <button className="btn btn-primary" onClick={saveCaptain}>
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
