import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");
  const [role, setRole] = useState("");

  // ✅ Re-run whenever route changes (after login/logout)
  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn") === "true";
    const storedName = localStorage.getItem("name") || "";
    const storedRole = localStorage.getItem("role") || "";

    setIsLoggedIn(loggedIn);
    setUserName(storedName.toUpperCase());
    setRole(storedRole);
  }, [location]); // listens for route change

  const handleLoginLogout = () => {
    if (isLoggedIn) {
      if (window.confirm("Are you sure you want to log out?")) {
        localStorage.clear();
        setIsLoggedIn(false);
        setUserName("");
        setRole("");
        navigate("/login");
      }
    } else {
      navigate("/login");
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow">
      <div className="container">
        <span className="navbar-brand">
          {!isLoggedIn ? "Cricket App" : `Welcome, ${userName}`}
        </span>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            {isLoggedIn && (
              <>
                <li className="nav-item">
                  <Link className="nav-link" to="/">
                    Tournament
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/team">
                    Team
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/player">
                    Player
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/match">
                    Match
                  </Link>
                </li>
              </>
            )}

            {/* Show Admin-only menu */}
            {isLoggedIn && role.toLowerCase() === "admin" && (
              <li className="nav-item">
                <Link className="nav-link text-warning" to="/admin">
                  Admin Panel
                </Link>
              </li>
            )}

            {/* Login/Logout button */}
            <li className="nav-item">
              <button
                className={`btn ${
                  isLoggedIn ? "btn-danger" : "btn-outline-warning"
                } ms-2`}
                onClick={handleLoginLogout}
              >
                {isLoggedIn ? "Logout" : "Login"}
              </button>
            </li>

            {/* Show Register only when not logged in */}
            {!isLoggedIn && (
              <li className="nav-item">
                <Link className="btn btn-outline-light ms-2" to="/register">
                  Register
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}
