import React, { useState } from "react";

export default function TeamForm() {
  const [formData, setFormData] = useState({
    tournamentId: "",
    teamname: "",
    logoUrl: "",
    city: "",
    captainName: "",
    captainPhone: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:8080/team/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      alert("Team created successfully!");
      setFormData({
        tournamentId: "",
        teamname: "",
        logoUrl: "",
        city: "",
        captainName: "",
        captainPhone: ""
      });
    } catch (error) {
      console.error("Error creating team", error);
      alert("Error creating team");
    }
  };

  const styles = {
    container: {
      maxWidth: "500px",
      margin: "30px auto",
      padding: "20px",
      backgroundColor: "#f9f9f9",
      borderRadius: "8px",
      boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)"
    },
    title: {
      textAlign: "center",
      color: "#333",
      marginBottom: "20px"
    },
    label: {
      display: "block",
      marginBottom: "6px",
      fontWeight: "bold",
      color: "#444"
    },
    input: {
      width: "100%",
      padding: "10px",
      marginBottom: "15px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      fontSize: "14px"
    },
    button: {
      width: "100%",
      padding: "12px",
      backgroundColor: "#2196F3",
      color: "#fff",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold"
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Register Team</h2>
      <form onSubmit={handleSubmit}>
        <label style={styles.label}>Tournament ID:</label>
        <input
          type="number"
          name="tournamentId"
          value={formData.tournamentId}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Team Name:</label>
        <input
          type="text"
          name="teamname"
          value={formData.teamname}
          onChange={handleChange}
          style={styles.input}
          required
        />

        <label style={styles.label}>Logo URL:</label>
        <input
          type="text"
          name="logoUrl"
          value={formData.logoUrl}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>City:</label>
        <input
          type="text"
          name="city"
          value={formData.city}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Captain Name:</label>
        <input
          type="text"
          name="captainName"
          value={formData.captainName}
          onChange={handleChange}
          style={styles.input}
        />

        <label style={styles.label}>Captain Phone:</label>
        <input
          type="text"
          name="captainPhone"
          value={formData.captainPhone}
          onChange={handleChange}
          style={styles.input}
        />

        <button type="submit" style={styles.button}>
          Save Team
        </button>
      </form>
    </div>
  );
}
