import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeamForm = () => {
  const [tournaments, setTournaments] = useState([]);
  const [teamData, setTeamData] = useState({
    name: '',
    logoUrl: '',
    city: '',
    captainName: '',
    captainPhone: '',
    tournamentId: '',
    players: [{ name: '', role: '' }]
  });

  useEffect(() => {
    axios.get('http://localhost:8080/api/tournaments').then(res => setTournaments(res.data));
  }, []);

  const handleChange = (e) => setTeamData({ ...teamData, [e.target.name]: e.target.value });

  const handlePlayerChange = (index, e) => {
    const updatedPlayers = [...teamData.players];
    updatedPlayers[index][e.target.name] = e.target.value;
    setTeamData({ ...teamData, players: updatedPlayers });
  };

  const addPlayer = () => setTeamData({ ...teamData, players: [...teamData.players, { name: '', role: '' }] });

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const response = await fetch('http://localhost:8080/api/teams', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(teamData)
    });

    if (response.ok) {
      alert('Team registered!');
    } else {
      const errorData = await response.json();
      alert('Error: ' + errorData.message);
    }
  } catch (error) {
    console.error('Fetch error:', error);
    alert('Network error while registering team');
  }
};


  return (
    <form onSubmit={handleSubmit}>
      <h2>Register Team</h2>
      {['name', 'logoUrl', 'city', 'captainName', 'captainPhone'].map((field) => (
        <input key={field} name={field} placeholder={field} onChange={handleChange} required />
      ))}
      <select name="tournamentId" onChange={handleChange} required>
        <option value="">Select Tournament</option>
        {tournaments.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
      </select>

      <h3>Players</h3>
      {teamData.players.map((player, index) => (
        <div key={index}>
          <input name="name" placeholder="Player Name" onChange={(e) => handlePlayerChange(index, e)} required />
          <input name="role" placeholder="Role" onChange={(e) => handlePlayerChange(index, e)} required />
        </div>
      ))}
      <button type="button" onClick={addPlayer}>Add Player</button>
      <button type="submit">Register Team</button>
    </form>
  );
};

export default TeamForm;
