import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function CaptainManage() {
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();
  const mob = localStorage.getItem("mobile");

  // Fetch captain's teams
  useEffect(() => {
    fetchTeams();
  }, [mob]);

  const fetchTeams = () => {
    fetch(`http://localhost:8081/team/fetchByCaptainPhone?phone=${mob}`)
      .then((res) => res.json())
      .then((data) => setTeams(data))
      .catch((err) => console.error("Error fetching teams:", err));
  };

  // Delete team
  const deleteTeam = (id) => {
    if (window.confirm("Are you sure you want to delete this team?")) {
      fetch(`http://localhost:8081/team/delete/${id}`, { method: "DELETE" })
        .then(() => {
          alert("Team deleted successfully!");
          fetchTeams();
        })
        .catch((err) => console.error("Error deleting team:", err));
    }
  };

  // Save edited team
  const saveTeam = () => {
    fetch(`http://localhost:8081/team/update/${selectedTeam.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(selectedTeam),
    })
      .then(() => {
        alert("Team updated successfully!");
        setShowModal(false);
        fetchTeams();
      })
      .catch((err) => console.error("Error updating team:", err));
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4 text-primary">Captain Manage</h2>

      <div className="row">
        {teams.map((team) => (
          <div className="col-md-4 mb-4" key={team.id}>
            <div className="card shadow-sm h-100">
              <img
                src={team.logoUrl || "https://via.placeholder.com/200"}
                className="card-img-top"
                alt={team.teamname}
                style={{ height: "200px", objectFit: "cover" }}
              />

              <div className="card-body">
                <h5 className="card-title">{team.teamname}</h5>
                <p className="text-muted">üìç {team.city}</p>
                <p className="mb-1">üë§ Captain: {team.captainName}</p>
                <p className="mb-1">üìû {team.captainPhone}</p>
                <p className="mb-1">üèÜ Tournament ID: {team.tournamentId}</p>
              </div>

              <div className="card-footer d-flex justify-content-between">
                <button
                  className="btn btn-warning btn-sm"
                  onClick={() => {
                    setSelectedTeam(team);
                    setShowModal(true);
                  }}
                >
                  ‚úè Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => deleteTeam(team.id)}
                >
                  üóë Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {teams.length === 0 && (
        <p className="text-center text-muted">No teams found for this captain.</p>
      )}

      {/* Edit Modal */}
      {showModal && selectedTeam && (
        <div
          className="modal fade show"
          style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Team</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <label>Team Name</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedTeam.teamname}
                  onChange={(e) =>
                    setSelectedTeam({ ...selectedTeam, teamname: e.target.value })
                  }
                />
                <label>City</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedTeam.city}
                  onChange={(e) =>
                    setSelectedTeam({ ...selectedTeam, city: e.target.value })
                  }
                />
                <label>Logo URL</label>
                <input
                  type="text"
                  className="form-control mb-2"
                  value={selectedTeam.logoUrl}
                  onChange={(e) =>
                    setSelectedTeam({ ...selectedTeam, logoUrl: e.target.value })
                  }
                />
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowModal(false)}
                >
                  Close
                </button>
                <button className="btn btn-primary" onClick={saveTeam}>
                  Save Changes
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
