import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import TournamentForm from "./TournamentForm";
import TeamForm from "./TeamForm";
import PlayerForm from "./PlayerForm";
import MatchForm from "./MatchForm";
import Profile from "./Profile"; // Create if not exists
import "./NavbarDashboard.css"; // Optional custom styling

function Navbar() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");
  const [role, setRole] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");

  const [showTournamentModal, setShowTournamentModal] = useState(false);
  const [showTeamModal, setShowTeamModal] = useState(false);
  const [showPlayerModal, setShowPlayerModal] = useState(false);
  const [showMatchModal, setShowMatchModal] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [showLogoutConfirmModal, setShowLogoutConfirmModal] = useState(false);

  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn") === "true";
    const storedName = localStorage.getItem("name") || "";
    const storedRole = localStorage.getItem("role") || "";

    setIsLoggedIn(loggedIn);
    setUserName(storedName.toUpperCase());
    setRole(storedRole);
  }, []);

  const showModalMessage = (message) => {
    setModalMessage(message);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setModalMessage("");
  };

  const handleLoginLogout = () => {
    if (isLoggedIn) {
      setShowLogoutConfirmModal(true);
    } else {
      navigate("/login");
    }
  };

  const confirmLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
    setUserName("");
    setRole("");
    setShowLogoutConfirmModal(false);
    navigate("/");
  };

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top shadow">
        <div className="container-fluid">
          <span className="navbar-brand">
            {!isLoggedIn ? "Cricket App" : `Welcome, ${userName}`}
          </span>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              {/* Tournament */}
              <button
                className="btn btn-outline-light me-2 my-1"
                onClick={() =>
                  isLoggedIn
                    ? setShowTournamentModal(true)
                    : showModalMessage("Please login first to manage tournaments.")
                }
              >
                Tournament
              </button>

              {/* Team */}
              <button
                className="btn btn-outline-light me-2 my-1"
                onClick={() =>
                  isLoggedIn
                    ? setShowTeamModal(true)
                    : showModalMessage("Please login first to manage teams.")
                }
              >
                Team
              </button>

              {/* Player */}
              <button
                className="btn btn-outline-light me-2 my-1"
                onClick={() =>
                  isLoggedIn
                    ? setShowPlayerModal(true)
                    : showModalMessage("Please login first to manage players.")
                }
              >
                Player
              </button>

              {/* Match */}
              <button
                className="btn btn-outline-light me-2 my-1"
                onClick={() =>
                  isLoggedIn
                    ? setShowMatchModal(true)
                    : showModalMessage("Please login first to manage matches.")
                }
              >
                Match
              </button>

              {/* Admin Panel */}
              {role.toLowerCase() === "admin" && (
                <button
                  className="btn btn-outline-warning me-2 my-1"
                  onClick={() => navigate("/admin")}
                >
                  Admin Panel
                </button>
              )}

              {/* My Profile */}
              {(role.toLowerCase() === "admin" || role.toLowerCase() === "user") && (
                <button
                  className="btn btn-outline-info me-2 my-1"
                  onClick={() => setShowProfileModal(true)}
                >
                  My Profile
                </button>
              )}

              {/* Login/Logout */}
              <button
                className={`btn ${
                  isLoggedIn ? "btn-danger" : "btn-outline-warning"
                } me-2 my-1`}
                onClick={handleLoginLogout}
              >
                {isLoggedIn ? "Logout" : "Login"}
              </button>

              {/* Register */}
              {!isLoggedIn && (
                <button
                  className="btn btn-outline-light me-2 my-1"
                  onClick={() => navigate("/register")}
                >
                  Register
                </button>
              )}
            </ul>
          </div>
        </div>
      </nav>

      {/* Tournament Modal */}
      {showTournamentModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">Tournament</h5>
                <button className="btn-close btn-close-white" onClick={() => setShowTournamentModal(false)} />
              </div>
              <div className="modal-body">
                <TournamentForm isModal onClose={() => setShowTournamentModal(false)} />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Team Modal */}
      {showTeamModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">Team</h5>
                <button className="btn-close btn-close-white" onClick={() => setShowTeamModal(false)} />
              </div>
              <div className="modal-body">
                <TeamForm isModal onClose={() => setShowTeamModal(false)} />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Player Modal */}
      {showPlayerModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">Player</h5>
                <button className="btn-close btn-close-white" onClick={() => setShowPlayerModal(false)} />
              </div>
              <div className="modal-body">
                <PlayerForm isModal onClose={() => setShowPlayerModal(false)} />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Match Modal */}
      {showMatchModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-success text-white">
                <h5 className="modal-title">Match</h5>
                <button className="btn-close btn-close-white" onClick={() => setShowMatchModal(false)} />
              </div>
              <div className="modal-body">
                <MatchForm isModal onClose={() => setShowMatchModal(false)} />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Profile Modal */}
      {/* {showProfileModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-info text-white">
                <h5 className="modal-title">My Profile</h5>
                <button className="btn-close btn-close-white" onClick={() => setShowProfileModal(false)} />
              </div>
              <div className="modal-body">
                <Profile />
              </div>
            </div>
          </div>
        </div>
      )} */}

      {/* Notice Modal */}
      {showModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Notice</h5>
                <button className="btn-close" onClick={closeModal} />
              </div>
              <div className="modal-body">
                <p>{modalMessage}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Logout Confirmation Modal */}
      {showLogoutConfirmModal && (
        <div className="modal fade show d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header bg-danger text-white">
                <h5 className="modal-title">Confirm Logout</h5>
              </div>
              <div className="modal-body">
                <p>Are you sure you want to log out?</p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowLogoutConfirmModal(false)}>Cancel</button>
                <button className="btn btn-danger" onClick={confirmLogout}>Yes, Logout</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default Navbar;
